/**
 * Entrada: vários itens vindos do Drive, cada um com { id, name }
 * Saída  : lista única de objetos { id, name } sem repetição de name
 */
const items = $input.all().map(i => i.json);

const seen = new Set();
const unique = [];

for (const item of items) {
  if (!seen.has(item.name)) {
    seen.add(item.name);
    unique.push(item);
  }
}

// n8n espera um array de { json: {...} }
return unique.map(item => ({ json: item }));

// PrepareAndFilter (corrigido)
let period;

// 1) Detecta o period ativo
try { period = $items("Set semana")[0].json.period; } catch {}
if (!period) {
  try { period = $items("Set quinzena")[0].json.period; } catch {}
}
if (!period) {
  try { period = $items("Set Mes")[0].json.period; } catch {}
}

// 2) Agrega todos os arquivos vindos de REMOVE DUPLICATES
const filesFound = $input.all().map(item => ({
  id:   item.json.id,
  name: item.json.name,
}));

// 3) Calcula ano, mês, semana ISO e quinzena para o sufixo
const now   = new Date();
const pad   = n => String(n).padStart(2,'0');
const year  = now.getUTCFullYear();
const month = pad(now.getUTCMonth() + 1);

const isoWeek = (() => {
  const tmp = new Date(Date.UTC(year, now.getUTCMonth(), now.getUTCDate()));
  tmp.setUTCDate(tmp.getUTCDate() + 4 - (tmp.getUTCDay() || 7));
  const y0 = new Date(Date.UTC(tmp.getUTCFullYear(), 0, 1));
  return pad(Math.ceil((((tmp - y0) / 86400000) + 1) / 7));
})();

const quinza = now.getUTCDate() <= 15 ? '1' : '2';

// 4) Define o sufixo conforme o period (ordem corrigida)
let suffix;
if (period === 'semanal') {
  // ex: "S25-2025"
  suffix = `S${isoWeek}-${year}`;
} else if (period === 'quinzenal') {
  // ex: "Q206-2025"
  suffix = `Q${quinza}${month}-${year}`;
} else {
  // mensal: "06-2025"
  suffix = `${month}-${year}`;
}

// 5) Filtra só os arquivos cujo name inclua esse sufixo
const current = filesFound.filter(f => f.name.includes(suffix));

// 6) Retorna period + lista filtrada
return [
  {
    json: {
      period,
      filesFound: current,
    }
  }
];

// O agent colocou o JSON dentro de output, mas com ```json …```
// Vamos limpar e parsear.

const raw = $json.output;

// Tira os fences ```json e ```
const inside = (raw.match(/```json\s*([\s\S]*?)```/) || [])[1] || raw;

// Parseia
let data;
try {
  data = JSON.parse(inside);
} catch (e) {
  throw new Error('Falha ao parsear JSON do agent: ' + e.message);
}

// Se quiser reter o period original, você pode puxar de $items("PrepareAndFilter1")[0].json.period
const period = $items("PrepareAndFilter")[0].json.period;

// Retorna tudo pronto para o HTML Template ou Gmail
return [{
  json: {
    period,
    presentes: data.presentes,
    faltantes: data.faltantes,
  }
}];

/**
 * Gera o HTML final a partir de period, presentes e faltantes
 */
const { period, presentes, faltantes } = $json;

// Cabeçalho
let html = `<h2 style="font-family:Arial,sans-serif;">[${period.toUpperCase()}] Validação de arquivos</h2>\n\n`;

// Presentes
html += `<h3 style="font-family:Arial,sans-serif; margin-bottom:4px;">✅ Presentes</h3>\n`;
html += `<ul style="font-family:Arial,sans-serif; margin:0; padding-left:16px;">\n`;
for (const item of presentes) {
  html += `  <li>${item}</li>\n`;
}
html += `</ul>\n\n`;

// Faltantes
html += `<h3 style="font-family:Arial,sans-serif; margin-bottom:4px;">❌ Faltantes</h3>\n`;
html += `<ul style="font-family:Arial,sans-serif; margin:0; padding-left:16px;">\n`;
if (faltantes.length === 0 || (faltantes.length === 1 && faltantes[0] === 'Nenhum faltante')) {
  html += `  <li>Nenhum faltante</li>\n`;
} else {
  for (const desc of faltantes) {
    html += `  <li>${desc}</li>\n`;
  }
}
html += `</ul>`;

// Retorna o HTML pronto
return [{ json: { html } }];

// <Mapeamento arquivos>
/*
  Entrada : { period, tz }
  Saída   : [{ pastaId, filePattern, desc, startDate, endDate }]
*/
const { period, tz = 'UTC' } = $json;

// IDs vindos de variáveis globais
const IDS = {
  // REL_SEMANAL: $vars.GDRIVE_REL_SEMANAL,
  // REL_MENSAL:  $vars.GDRIVE_REL_MENSAL,
  // REU_SEMANAL: $vars.GDRIVE_REU_SEMANAL,
  // REU_QUINZ:   $vars.GDRIVE_REU_QUINZ,
  // REU_MENSAL:  $vars.GDRIVE_REU_MENSAL,
  PASTA_TEST:  $vars.PASTA_TEST,
  DROP : $vars.DROP_TESTE
};

// Helpers de data
const now    = new Date(new Date().toLocaleString('en-US', { timeZone: tz }));
const pad    = n => String(n).padStart(2, '0');
const year   = now.getUTCFullYear();
const month  = pad(now.getUTCMonth() + 1);

// Semana ISO
const isoWeek = (() => {
  const tmp = new Date(Date.UTC(year, now.getUTCMonth(), now.getUTCDate()));
  tmp.setUTCDate(tmp.getUTCDate() + 4 - (tmp.getUTCDay() || 7));
  const y0  = new Date(Date.UTC(tmp.getUTCFullYear(), 0, 1));
  return pad(Math.ceil((((tmp - y0) / 86400000) + 1) / 7));
})();

// Quinzena
const quinza = now.getUTCDate() <= 15 ? '1' : '2';

// Intervalo
const endDate = now.toISOString();
let startDate;
if (period === 'semanal')       startDate = new Date(now.getTime() - 7*86400000).toISOString();
else if (period === 'quinzenal') startDate = new Date(now.getTime() - 15*86400000).toISOString();
else                             startDate = new Date(year, now.getUTCMonth()-1, now.getUTCDate()).toISOString();

// Padrões com regex de extensão flexível
const catalogo = [
  // Semanal
  {
    pasta: IDS.DROP,
    filePattern: `^ATA_REUNIAO_OBRA-S${isoWeek}-${year}\\.(xlsx|pptx|docx)$`,
    desc: 'Ata reunião de obra',
    tipo: 'semanal',
  },
  {
    pasta: IDS.DROP,
    filePattern: `^PRESENCA_DSS-S${isoWeek}-${year}\\.(xlsx|pptx|docx)$`,
    desc: 'Lista presença DSS',
    tipo: 'semanal',
  },
  {
    pasta: IDS.DROP,
    filePattern: `^PRESENCA_TREINAMENTO-S${isoWeek}-${year}\\.(xlsx|pptx|docx)$`,
    desc: 'Lista presença integração treinamentos',
    tipo: 'semanal',
  },
  {
    pasta: IDS.DROP,
    filePattern: `^RELATORIO_SEG_TRAB-S${isoWeek}-${year}\\.(xlsx|pptx|docx)$`,
    desc: 'Segurança do trabalho',
    tipo: 'semanal',
  },
  {
    pasta: IDS.DROP,
    filePattern: `^PAINEL_PCP-S${isoWeek}-${year}\\.(xlsx|pptx|docx)$`,
    desc: 'Painel PCP',
    tipo: 'semanal',
  },
  {
    pasta: IDS.DROP,
    filePattern: `^RELATORIO_QUALIDADE-S${isoWeek}-${year}\\.(xlsx|pptx|docx)$`,
    desc: 'Relatório qualidade',
    tipo: 'semanal',
  },

  // Quinzenal
  {
    pasta: IDS.DROP,
    filePattern: `^REUNIAO_RESTRICOES-Q${quinza}${month}-${year}\\.(xlsx|pptx|docx)$`,
    desc: 'Ata reunião restrições',
    tipo: 'quinzenal',
  },
  {
    pasta: IDS.DROP,
    filePattern: `^PMP-Q${quinza}${month}-${year}\\.(xlsx|pptx|docx)$`,
    desc: 'PMP',
    tipo: 'quinzenal',
  },
  {
    pasta: IDS.DROP,
    filePattern: `^ATA_REUNIAO_PROJETOS-Q${quinza}${month}-${year}\\.(xlsx|pptx|docx)$`,
    desc: 'Ata reunião alinhamento projetos',
    tipo: 'quinzenal',
  },

  // Mensal
  {
    pasta: IDS.DROP,
    filePattern: `^APRESENTACAO_RESULTADOS-${month}-${year}\\.(xlsx|pptx|docx)$`,
    desc: 'Apresentação resultados',
    tipo: 'mensal',
  },
  {
    pasta: IDS.DROP,
    filePattern: `^AUDITORIA_SEG_TRABALHO-${month}-${year}\\.(xlsx|pptx|docx)$`,
    desc: 'Auditoria segurança do trabalho',
    tipo: 'mensal',
  },
  {
    pasta: IDS.DROP,
    filePattern: `^AUDITORIA_QUALIDADE-${month}-${year}\\.(xlsx|pptx|docx)$`,
    desc: 'Auditoria qualidade',
    tipo: 'mensal',
  },
  {
    pasta: IDS.DROP,
    filePattern: `^PAINEL_AVAL_TERCEIROS-${month}-${year}\\.(xlsx|pptx|docx)$`,
    desc: 'Painel avaliação terceiros',
    tipo: 'mensal',
  },
];

return catalogo
  .filter(t => t.tipo === period)
  .map(t => ({
    pastaId:    t.pasta,
    filePattern:t.filePattern,
    desc:       t.desc,
    startDate,
    endDate,
  }));
